import { useEffect, useState } from 'react';
import { useDispatch, useSelector,shallowEqual} from 'react-redux';
import { Next } from '../actions/semaforoAction';

export const AppBody = () => {

  const [buttonValues, setbuttonValues] = useState(['','','']);
  const [nextCount, setnextCount] = useState(0);
  const { count, valueArray } = useSelector( state => state.semaforo,shallowEqual);
  const dispatch = useDispatch();  

  const handleNext = (e) =>{
      e.preventDefault();   
      setnextCount(count+1);

      switch(nextCount){
        case 1:
             setbuttonValues(['rojo','','']);
            break
        case 2:
             setbuttonValues(['','amarillo','']);
            break
        case 3:
              setbuttonValues(['','','verde']);
              setnextCount(1);
            break
        default:
              setbuttonValues(['','','']);

      }
      // if(nextCount>=3){
      //   setnextCount(1);
      // }
  }

  useEffect(()=>{
    dispatch(Next(nextCount,valueArray));
  },[dispatch,nextCount,valueArray])

  console.log(count);
  return (
    <div>AppBody
        <h3>{nextCount}{buttonValues}</h3>
        <button 
            onClick={ handleNext }
        >Next</button>
    </div>
  )
}
