import { useEffect, useState } from 'react';
import { useDispatch, useSelector,shallowEqual} from 'react-redux';
import { Next } from '../actions/semaforoAction';
import { SemaforList } from './SemaforList';

export const AppBody = () => {

  const [buttonValues, setbuttonValues] = useState(['','','']);
  const [nextCount, setnextCount] = useState(0);
  const { count } = useSelector( state => state.semaforo,shallowEqual);
  const dispatch = useDispatch();  

  const handleNext = (e) =>{
      e.preventDefault();   
      setnextCount(count+1);
  }

  useEffect(()=>{
    dispatch(Next(nextCount,buttonValues));

    switch(nextCount){
      case 1:
           setbuttonValues(['rojo','','']);
          break
      case 2:
           setbuttonValues(['','amarillo','']);
          break
      case 3:
            setbuttonValues(['','','verde']);
            setnextCount(1);
          break
      default:
            setbuttonValues(['','','']);

    }

  },[dispatch,nextCount,buttonValues])

  console.log(count);
  return (
    <div>AppBody
        <h3>{nextCount}{buttonValues}</h3>
        <SemaforList buttonValues={buttonValues}/>  
        <button 
            onClick={ handleNext }
        >Next</button>
    </div>
  )
}
